import React, { useRef, useState } from "react";
import "../styles/Global.css";

// נתוני ה-JSON
const data = {
  SHAP: {
    Age: 30,
    "Heart rate": 12,
    "Blood pressure": 16,
    BMI: 10,
    Gender: 6,
    Fever: 6,
    Weight: 5,
  },
  FBT: {
    Age: 28,
    "Heart rate": 32,
    "Blood pressure": 16,
    BMI: 7,
    Gender: 7,
    Fever: 12,
    Weight: 4,
  },
};

const GraphContainer = () => {
  const graphRef = useRef(); // הפניה לתיבת הגרפים
  const [isFullscreen, setIsFullscreen] = useState(false); // מעקב אחרי מצב המסך
  const [activeGraph, setActiveGraph] = useState("Bar"); // מצב הכפתור הפעיל

  // פונקציה להפעלת מסך מלא
  const handleFullscreen = () => {
    if (!isFullscreen) {
      if (graphRef.current.requestFullscreen) {
        graphRef.current.requestFullscreen();
      } else if (graphRef.current.webkitRequestFullscreen) {
        // Safari
        graphRef.current.webkitRequestFullscreen();
      } else if (graphRef.current.msRequestFullscreen) {
        // IE/Edge
        graphRef.current.msRequestFullscreen();
      }
      setIsFullscreen(true); // עדכון מצב למסך מלא
    } else {
      if (document.exitFullscreen) {
        document.exitFullscreen();
      } else if (document.webkitExitFullscreen) {
        // Safari
        document.webkitExitFullscreen();
      } else if (document.msExitFullscreen) {
        // IE/Edge
        document.msExitFullscreen();
      }
      setIsFullscreen(false); // חזרה ממצב מסך מלא
    }
  };

  // פונקציה לטיפול בלחיצה על כפתור גרף
  const handleGraphChange = (graphType) => {
    setActiveGraph(graphType);
  };

  return (
    <div className="graph-container" ref={graphRef}>
      {/* שורת כפתורים */}
      <div className="graph-buttons-container">
        <div className="graph-buttons">
          <button
            className={`graph-button ${activeGraph === "Bar" ? "active" : ""}`}
            onClick={() => handleGraphChange("Bar")}
          >
            Bar Chart
          </button>
          <button
            className={`graph-button ${activeGraph === "Row" ? "active" : ""}`}
            onClick={() => handleGraphChange("Row")}
          >
            Row Chart
          </button>
        </div>
        <button className="fullscreen-button" onClick={handleFullscreen}>
          {isFullscreen ? "Exit Full Screen" : "Full Screen"}
        </button>
      </div>

      {/* אזור הגרף */}
      <div className="graph-display">
        {activeGraph === "Bar" && (
          <div className="bar-chart">
            {Object.keys(data.SHAP).map((key) => (
              <div key={key} className="bar-wrapper">
                <div className="bar" style={{ height: `${data.SHAP[key] * 5}px` }}>
                  {data.SHAP[key]}
                </div>
                <p>{key}</p>
              </div>
            ))}
          </div>
        )}

        {activeGraph === "Row" && (
          <div className="row-chart">
            {Object.keys(data.FBT).map((key) => (
              <div key={key} className="row-wrapper">
                <p>{key}</p>
                <div className="row" style={{ width: `${data.FBT[key] * 5}px` }}>
                  {data.FBT[key]}
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default GraphContainer;
