import React, { useState, useRef } from "react";
import "../styles/Global.css";
import data from "../JSON/global_graph_data.json"; // ייבוא קובץ JSON

const GraphContainer = () => {
  const graphRef = useRef();
  const [activeGraph, setActiveGraph] = useState("Row"); // הגרף הנוכחי
  const [isFullscreen, setIsFullscreen] = useState(false); // מצב מסך מלא

  // מיון הפיצ'רים לפי סדר ה-ABC
  const sortedKeys = Object.keys(data.SHAP).sort();

  // פונקציה להפעלת/יציאת מסך מלא
  const handleFullscreen = () => {
    if (!isFullscreen) {
      if (graphRef.current.requestFullscreen) {
        graphRef.current.requestFullscreen();
      } else if (graphRef.current.webkitRequestFullscreen) {
        // Safari
        graphRef.current.webkitRequestFullscreen();
      } else if (graphRef.current.msRequestFullscreen) {
        // IE/Edge
        graphRef.current.msRequestFullscreen();
      }
      setIsFullscreen(true);
    } else {
      if (document.exitFullscreen) {
        document.exitFullscreen();
      } else if (document.webkitExitFullscreen) {
        // Safari
        document.webkitExitFullscreen();
      } else if (document.msExitFullscreen) {
        // IE/Edge
        document.msExitFullscreen();
      }
      setIsFullscreen(false);
    }
  };

  // פונקציה לשינוי סוג הגרף
  const handleGraphChange = (graphType) => {
    setActiveGraph(graphType);
  };

  return (
    <div className="graph-container" ref={graphRef}>
      {/* שורת כפתורים */}
      <div className="graph-buttons-container">
        <button
          className={`graph-button ${activeGraph === "Row" ? "active" : ""}`}
          onClick={() => handleGraphChange("Row")}
        >
          Row
        </button>
        <button
          className={`graph-button ${activeGraph === "Radar" ? "active" : ""}`}
          onClick={() => handleGraphChange("Radar")}
        >
          Radar
        </button>

        <button className="fullscreen-button" onClick={handleFullscreen}>
          {isFullscreen ? "Exit Full Screen" : "Full Screen"}
        </button>
      </div>

      {/* גרפים עם גלילה */}
      <div className="graph-display">
        {activeGraph === "Row" && (
          <div className="row-chart">
            {sortedKeys.map((key) => (
              <div key={key} className="row-wrapper">
                <p className="label">{key}</p>
                <div className="row-bars">
                  <div
                    className="row-bar shap"
                    style={{
                      width: `${data.SHAP[key] * 10}px`,
                      backgroundColor: "#f39c12",
                    }}
                  >
                    {data.SHAP[key].toFixed(2)}
                  </div>
                  <div
                    className="row-bar fbt"
                    style={{
                      width: `${data.FBT[key] * 10}px`,
                      backgroundColor: "#27ae60",
                    }}
                  >
                    {data.FBT[key].toFixed(2)}
                  </div>
                  <div
                    className="row-bar logistic"
                    style={{
                      width: `${data["Logistic Regression"][key] * 10}px`,
                      backgroundColor: "#2980b9",
                    }}
                  >
                    {data["Logistic Regression"][key].toFixed(2)}
                  </div>
                  <div
                    className="row-bar decision"
                    style={{
                      width: `${data["Decision Tree"][key] * 10}px`,
                      backgroundColor: "#8e44ad",
                    }}
                  >
                    {data["Decision Tree"][key].toFixed(2)}
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* מקרא צבעים */}
      <div className="legend">
        <div className="legend-item">
          <div className="color-box" style={{ backgroundColor: "#f39c12" }}></div>
          <p>SHAP</p>
        </div>
        <div className="legend-item">
          <div className="color-box" style={{ backgroundColor: "#27ae60" }}></div>
          <p>FBT</p>
        </div>
        <div className="legend-item">
          <div className="color-box" style={{ backgroundColor: "#2980b9" }}></div>
          <p>Logistic Regression</p>
        </div>
        <div className="legend-item">
          <div className="color-box" style={{ backgroundColor: "#8e44ad" }}></div>
          <p>Decision Tree</p>
        </div>
      </div>
    </div>
  );
};

export default GraphContainer;
