import React, { useState } from "react";
import "../styles/Global.css";
import data from "../JSON/global graph data.json"; // ייבוא קובץ JSON

const GraphContainer = () => {
  const [activeGraph, setActiveGraph] = useState("Row"); // הגרף הנוכחי

  const handleGraphChange = (graphType) => {
    setActiveGraph(graphType);
  };

  return (
    <div className="graph-container">
      {/* כפתורים למעבר בין סוגי הגרפים */}
      <div className="graph-buttons-container">
        <button
          className={`graph-button ${activeGraph === "Row" ? "active" : ""}`}
          onClick={() => handleGraphChange("Row")}
        >
          Row
        </button>
        <button
          className={`graph-button ${activeGraph === "Radar" ? "active" : ""}`}
          onClick={() => handleGraphChange("Radar")}
        >
          Radar
        </button>
      </div>

      {/* גרף */}
      <div className="graph-display">
        {activeGraph === "Row" && (
          <div className="row-chart">
            {Object.keys(data.SHAP).map((key) => (
              <div key={key} className="row-wrapper">
                <p className="label">{key}</p>
                <div className="row-bars">
                  <div
                    className="row-bar shap"
                    style={{ width: `${data.SHAP[key] * 10}px` }}
                  >
                    {data.SHAP[key]}
                  </div>
                  <div
                    className="row-bar fbt"
                    style={{ width: `${data.FBT[key] * 10}px` }}
                  >
                    {data.FBT[key]}
                  </div>
                  <div
                    className="row-bar logistic"
                    style={{
                      width: `${data["Logistic Regression"][key] * 10}px`,
                    }}
                  >
                    {data["Logistic Regression"][key]}
                  </div>
                  <div
                    className="row-bar decision"
                    style={{ width: `${data["Decision Tree"][key] * 10}px` }}
                  >
                    {data["Decision Tree"][key]}
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default GraphContainer;
